var words = ['برنامه نویس', 'فریلنسر', 'متخصص لینوکس', 'مسلط به چند زبان', 'طراحی سایت', 'طراحی اپلیکیشن'];
var wordIndex = 0;
var letterIndex = 0;
var delay = 100;

function typeLetter() {
    var element = document.querySelector('.typed');
    var currentWord = words[wordIndex];
    var currentLetters = currentWord.slice(0, letterIndex + 1);

    element.textContent = currentLetters;

    if (letterIndex < currentWord.length - 1) {
        letterIndex++;
        setTimeout(typeLetter, delay);
    } else {
        letterIndex = 0;
        wordIndex = (wordIndex + 1) % words.length;
        setTimeout(startTyping, 2000);
    }
}

function startTyping() {
    setTimeout(typeLetter, delay);
}

startTyping();